import React, { useState } from "react";

const Contact = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/send-email", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    const result = await response.json();
    alert(result.message);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto bg-gray-100 rounded-lg shadow-lg">
      {/* Section 1: Contact Info */}
      <div className="text-center mb-6">
        <h2 className="text-2xl font-bold">Let's Connect</h2>
        <p className="text-gray-700">Feel free to reach out to me!</p>
        <p className="mt-2 text-lg font-semibold">Email: your_email@example.com</p>
        <p className="text-lg font-semibold">Phone: +1234567890</p>
        <p className="text-lg font-semibold">Location: Your City, Country</p>
        <div className="flex justify-center mt-4 space-x-4">
          <a href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer">GitHub</a>
          <a href="https://linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer">LinkedIn</a>
          <a href="https://twitter.com/yourusername" target="_blank" rel="noopener noreferrer">X</a>
        </div>
      </div>

      {/* Section 2: Contact Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" name="fullName" placeholder="Full Name" onChange={handleChange} required className="w-full p-2 border rounded" />
        <input type="email" name="email" placeholder="Your Email" onChange={handleChange} required className="w-full p-2 border rounded" />
        <textarea name="message" placeholder="Your Message" onChange={handleChange} required className="w-full p-2 border rounded h-24"></textarea>
        <button type="submit" className="w-full p-2 bg-blue-500 text-white rounded">Send Message</button>
      </form>
    </div>
  );
};

export default Contact;
