import React from 'react';
import { TypeAnimation } from 'react-type-animation';

const Home = () => {
  return (
    <div className="p-4 lg:p-8 bg-gray-100 dark:bg-gray-900 min-h-screen flex flex-col lg:flex-row items-center lg:items-start">
      {/* Image Section */}
      <div className="w-full lg:w-1/3 flex justify-center lg:justify-start mb-8 lg:mb-0">
      <img
  src={profileImage}
  alt="Law Bind Pandey"
  className="rounded-full w-48 h-48 lg:w-64 lg:h-64 object-cover shadow-lg"
/>
      </div>

      {/* Text Section */}
      <div className="w-full lg:w-2/3 lg:pl-8">
        <h1 className="text-2xl md:text-4xl font-bold text-gray-800 dark:text-white mb-4">
          I am Law Bind Pandey,{' '}
          <TypeAnimation
            sequence={[
              'Software Engineer',
              1000, // Delay after typing
              'AI/ML Enthusiast',
              1000,
              'Mathematics Lover',
              1000,
            ]}
            wrapper="span"
            speed={50} // Typing speed
            style={{ display: 'inline-block' }}
            repeat={Infinity} // Repeat the animation infinitely
          />
        </h1>
        <p className="text-lg md:text-xl text-gray-600 dark:text-gray-300 mb-4">
          A mathematics enthusiast with a good understanding of AI/ML and a research-oriented mindset. With 2 years of experience as a software developer in .NET, I aspire to become a machine learning engineer. I also have a strong foundation in Data Structures and Algorithms (DSA).
        </p>
        <p className="text-lg md:text-xl text-gray-600 dark:text-gray-300">
          Let's Connect!
        </p>
      </div>
    </div>
  );
};

export default Home;
