import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/blogs')
      .then(response => setBlogs(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div className="flex bg-gray-100 dark:bg-gray-900 min-h-screen">
      {/* Left Panel */}
      <div className="w-1/4 bg-gray-200 dark:bg-gray-800 p-4">
        <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-4">Blogs</h2>
        <ul>
          {blogs.map(blog => (
            <li key={blog.id} className="mb-2">
              <Link
                to={`/blogs/${blog.id}`}
                className="text-blue-500 dark:text-blue-400 hover:text-blue-600 dark:hover:text-blue-300 transition duration-300"
              >
                {blog.title}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="w-3/4 p-4">
        {/* Blog content will be displayed here */}
      </div>
    </div>
  );
};

export default Blogs;
