import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [isPanelOpen, setIsPanelOpen] = useState(true); // State to manage panel visibility

  useEffect(() => {
    axios.get('http://localhost:5000/api/blogs')
      .then(response => setBlogs(response.data))
      .catch(error => console.error(error));
  }, []);

  const togglePanel = () => {
    setIsPanelOpen(!isPanelOpen);
  };

  return (
    <div className="flex flex-col lg:flex-row bg-gray-100 dark:bg-gray-900 min-h-screen">
      {/* Collapsible Button (Mobile) */}
      <button
        onClick={togglePanel}
        className="lg:hidden p-2 bg-blue-500 text-white text-center focus:outline-none"
      >
        {isPanelOpen ? 'Hide Blogs' : 'Show Blogs'}
      </button>

      {/* Left Panel */}
      <div
        className={`w-full lg:w-1/4 bg-gray-200 dark:bg-gray-800 p-4 transition-all duration-300 ${
          isPanelOpen ? 'block' : 'hidden'
        } lg:block`}
      >
        <h2 className="text-xl font-bold text-gray-800 dark:text-white mb-4">Blogs</h2>
        <ul>
          {blogs.map(blog => (
            <li key={blog.id} className="mb-2">
              <Link
                to={`/blogs/${blog.id}`}
                className="text-blue-500 dark:text-blue-400 hover:text-blue-600 dark:hover:text-blue-300 transition duration-300"
              >
                {blog.title}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className={`w-full ${isPanelOpen ? 'lg:w-3/4' : 'lg:w-full'} p-4 lg:p-8`}>
        {/* Blog content will be displayed here */}
        <h1 className="text-2xl font-bold text-gray-800 dark:text-white mb-4">Blog Content</h1>
        <p className="text-gray-600 dark:text-gray-300">
          Select a blog from the left panel to view its content.
        </p>
      </div>
    </div>
  );
};

export default Blogs;
